// Final Working CSV Parser - Correctly accesses n8n HTTP response
console.log("üîç Starting CSV analysis...");

let data = [];
let datasetSource = "CSV Dataset";

try {
  // Get the HTTP response
  const httpResponse = $input.first();

  if (!httpResponse || !httpResponse.json) {
    throw new Error("No response from HTTP Request");
  }

  // The CSV data is in the "data" field
  const csvText = httpResponse.json.data;

  if (!csvText || typeof csvText !== 'string') {
    throw new Error("No CSV text found in response data field");
  }

  console.log(`üìÑ CSV text length: ${csvText.length} characters`);

  // Parse CSV manually
  const lines = csvText.split('\n').filter(line => line.trim().length > 0);
  console.log(`üìä Found ${lines.length} lines in CSV`);

  if (lines.length < 2) {
    throw new Error("CSV must have at least header and one data row");
  }

  // Parse header row - handle quoted fields properly
  const headerLine = lines[0];
  const headers = headerLine.split(',').map(header => header.trim().replace(/^"|"$/g, ''));
  console.log(`üìã Headers (${headers.length}): ${headers.join(', ')}`);

  // Parse data rows with better CSV parsing
  for (let i = 1; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line) continue;

    // Simple CSV parsing - split by comma but handle quoted fields
    const values = [];
    let current = '';
    let inQuotes = false;

    for (let j = 0; j < line.length; j++) {
      const char = line[j];

      if (char === '"') {
        inQuotes = !inQuotes;
      } else if (char === ',' && !inQuotes) {
        values.push(current.trim());
        current = '';
      } else {
        current += char;
      }
    }
    values.push(current.trim()); // Add the last field

    // Create row object if we have the right number of fields
    if (values.length === headers.length) {
      const row = {};
      headers.forEach((header, index) => {
        let value = values[index].replace(/^"|"$/g, ''); // Remove quotes

        // Try to convert numbers
        if (value && !isNaN(value) && value !== '' && !isNaN(parseFloat(value))) {
          value = parseFloat(value);
        }

        // Convert empty strings to null for missing data detection
        if (value === '') {
          value = null;
        }

        row[header] = value;
      });
      data.push(row);
    }
  }

  console.log(`‚úÖ Successfully parsed ${data.length} records`);

  // Detect dataset type
  if (headers.includes('PassengerId') || headers.includes('Name')) {
    datasetSource = "Titanic Dataset";
  } else if (headers.includes('Species') || headers.includes('species')) {
    datasetSource = "Iris Dataset";
  } else {
    datasetSource = "CSV Dataset";
  }

  console.log(`üìà Dataset: ${datasetSource}`);

} catch (error) {
  console.log(`‚ùå Error parsing CSV: ${error.message}`);

  return [{
    json: {
      error: `Failed to parse CSV: ${error.message}`,
      totalRows: 0,
      totalColumns: 0,
      columnsWithMissing: [],
      overallStats: {
        columnsWithMissingData: 0,
        columnsWithCompleteData: 0,
        averageMissingPercentage: 0,
        dataQualityScore: 0,
        severityBreakdown: { critical: 0, high: 0, medium: 0, low: 0 }
      },
      recommendations: [
        "üö® CSV Parsing Error:",
        `‚Ä¢ ${error.message}`,
        "üí° Check the CSV URL and format"
      ],
      datasetInfo: {
        source: "Error - Parse Failed",
        downloadedAt: new Date().toISOString(),
        processor: "n8n CSV Parser"
      }
    }
  }];
}

// Initialize analysis results
const analysis = {
  totalRows: data.length,
  totalColumns: 0,
  columnsWithMissing: [],
  overallStats: {},
  recommendations: [],
  datasetInfo: {
    source: datasetSource,
    downloadedAt: new Date().toISOString(),
    processor: "n8n CSV Parser"
  }
};

if (data.length === 0) {
  analysis.error = "No data rows found after parsing";
  analysis.recommendations.push("üö® CSV appears to have no data rows");
  return [{ json: analysis }];
}

// Get column information
const columns = Object.keys(data[0]);
analysis.totalColumns = columns.length;

console.log(`üìä Analyzing ${columns.length} columns across ${data.length} records...`);

// Analyze each column for missing values
columns.forEach((column, index) => {
  let missingCount = 0;
  let totalValues = data.length;
  let sampleValues = [];

  data.forEach(row => {
    const value = row[column];

    // Collect sample non-missing values (first 3)
    if (sampleValues.length < 3 && value !== null && value !== undefined && value !== '') {
      sampleValues.push(String(value));
    }

    // Check for missing values - comprehensive check
    if (value === null || 
        value === undefined || 
        value === '' || 
        value === 'null' || 
        value === 'NULL' || 
        value === 'N/A' || 
        value === 'n/a' ||
        value === 'NA' ||
        value === '#N/A' ||
        value === 'None' ||
        (typeof value === 'string' && value.trim() === '')) {
      missingCount++;
    }
  });

  const missingPercentage = (missingCount / totalValues) * 100;

  // Only track columns that actually have missing data
  if (missingCount > 0) {
    analysis.columnsWithMissing.push({
      column: column,
      missingCount: missingCount,
      totalValues: totalValues,
      missingPercentage: parseFloat(missingPercentage.toFixed(2)),
      completenessPercentage: parseFloat((100 - missingPercentage).toFixed(2)),
      sampleValues: sampleValues,
      dataType: typeof data[0][column],
      severity: missingPercentage > 50 ? 'Critical' : missingPercentage > 20 ? 'High' : missingPercentage > 5 ? 'Medium' : 'Low'
    });
  }
});

// Sort missing columns by severity (highest missing percentage first)
analysis.columnsWithMissing.sort((a, b) => b.missingPercentage - a.missingPercentage);

// Calculate overall statistics
const totalMissingColumns = analysis.columnsWithMissing.length;
const avgMissingPercentage = totalMissingColumns > 0 
  ? analysis.columnsWithMissing.reduce((sum, col) => sum + col.missingPercentage, 0) / totalMissingColumns
  : 0;

const severityBreakdown = {
  critical: analysis.columnsWithMissing.filter(col => col.severity === 'Critical').length,
  high: analysis.columnsWithMissing.filter(col => col.severity === 'High').length,
  medium: analysis.columnsWithMissing.filter(col => col.severity === 'Medium').length,
  low: analysis.columnsWithMissing.filter(col => col.severity === 'Low').length
};

analysis.overallStats = {
  columnsWithMissingData: totalMissingColumns,
  columnsWithCompleteData: analysis.totalColumns - totalMissingColumns,
  averageMissingPercentage: parseFloat(avgMissingPercentage.toFixed(2)),
  dataQualityScore: parseFloat((100 - avgMissingPercentage).toFixed(2)),
  severityBreakdown: severityBreakdown,
  worstColumn: analysis.columnsWithMissing.length > 0 ? analysis.columnsWithMissing[0] : null,
  bestIncompleteColumn: analysis.columnsWithMissing.length > 0 ? analysis.columnsWithMissing[analysis.columnsWithMissing.length - 1] : null
};

// Generate actionable recommendations
if (totalMissingColumns === 0) {
  analysis.recommendations.push('üéâ PERFECT: All columns are complete with no missing values!');
  analysis.recommendations.push('‚úÖ This dataset is ready for immediate analysis and modeling');
} else {
  // Overall quality assessment
  if (analysis.overallStats.dataQualityScore >= 90) {
    analysis.recommendations.push('üéâ EXCELLENT: Dataset has outstanding quality with minimal missing data');
  } else if (analysis.overallStats.dataQualityScore >= 80) {
    analysis.recommendations.push('‚úÖ VERY GOOD: Dataset quality is high with minor data gaps');
  } else if (analysis.overallStats.dataQualityScore >= 70) {
    analysis.recommendations.push('üëç GOOD: Dataset quality is acceptable with some cleaning needed');
  } else if (analysis.overallStats.dataQualityScore >= 60) {
    analysis.recommendations.push('‚ö†Ô∏è FAIR: Dataset needs moderate cleaning before analysis');
  } else {
    analysis.recommendations.push('üö® POOR: Dataset requires significant data quality improvements');
  }

  // Specific column recommendations - show top 5 worst
  const topWorstColumns = analysis.columnsWithMissing.slice(0, 5);
  topWorstColumns.forEach(col => {
    if (col.missingPercentage > 70) {
      analysis.recommendations.push(`üóëÔ∏è CONSIDER DROPPING: '${col.column}' (${col.missingPercentage}% missing)`);
    } else if (col.missingPercentage > 30) {
      analysis.recommendations.push(`üîß NEEDS ATTENTION: '${col.column}' (${col.missingPercentage}% missing)`);
    } else if (col.missingPercentage > 10) {
      analysis.recommendations.push(`‚ö° MODERATE CLEANUP: '${col.column}' (${col.missingPercentage}% missing)`);
    } else {
      analysis.recommendations.push(`‚ú® MINOR TOUCH-UP: '${col.column}' (${col.missingPercentage}% missing)`);
    }
  });

  // Action items
  if (severityBreakdown.critical > 0) {
    analysis.recommendations.push(`üìã IMMEDIATE ACTION: Review ${severityBreakdown.critical} critical column(s) for removal`);
  }
  if (severityBreakdown.high > 0) {
    analysis.recommendations.push(`üìã STRATEGY NEEDED: Develop plan for ${severityBreakdown.high} high-missing column(s)`);
  }
}

console.log(`‚úÖ Analysis Complete!`);
console.log(`üìä ${analysis.totalRows} rows √ó ${analysis.totalColumns} columns`);
console.log(`üéØ Data Quality Score: ${analysis.overallStats.dataQualityScore}%`);
console.log(`üìâ Missing data found in ${totalMissingColumns} of ${analysis.totalColumns} columns`);

return [{ json: analysis }];
